function options(pallete = 'pallete1',type='area',title='Monthly Sales',subtitle='Monthly movement sales',height=400,series_data,categories){
    return {
        theme: {
            palette: pallete,
        },
        chart: {
          type: type,
          fontFamily: 'Inter, Arial, sans-serif',
          height: height,
          zoom: {
            enabled: false
          },
        },
        series: series_data,
        xaxis: {
          categories: categories,
        },
        title: {
            text: title
        },
        subtitle: {
            text: subtitle
        },
        stroke: {
            curve: 'smooth'
        },
    }
}

// Donut Option Helper
function optionsDonut(pallete,type,title,subtitle,height,series_data,categories){
    return {
        theme: {
            palette: pallete,
        },
        chart: {
          type: type,
          fontFamily: 'Inter, Arial, sans-serif',
          height: height,
          zoom: {
            enabled: false
          },
        },
        series: series_data,
        labels: categories,
        xaxis: {
          categories: categories,
        },
        title: {
            text: title
        },
        subtitle: {
            text: subtitle
        },
        stroke: {
            curve: 'smooth'
        },
        legend:{
            show: false,
        },
        plotOptions: {
            pie: {
              donut: {
                size: '60%'
              }
            }
        },
        responsive: [
          {
            breakpoint: 1000,
            options: {
              plotOptions: {
                bar: {
                  horizontal: false
                }
              },
              legend: {
                show: false,
                position: "bottom"
              }
            }
          }
        ]
    }
}